const Noticia = ({ noticia }) => {
  const { urlToImagen, url, title, description, source } = noticia;

  let imagen;
  if (urlToImagen != "") {
    imagen = <img src={urlToImagen} alt={title} className="card-img-top" />;
  }else{
    imagen = <p className='text-center'>Imagen no disponible</p>
  }
  return (
    <div className="col-md-6 col-12 mb-4">
      <div className="card">
        <div className="card-image">{imagen}</div>
      </div>
    </div>
  );
};

const Noticias = ({ noticias }) => {
  return (
    <div className="row">
      {noticias.map((noticia, i) => (
        <Noticia key={i} noticia={noticia} />
      ))}
    </div>
  );
};

export default Noticias;
