import fetch from "isomorphic-unfetch";

import MasterPage from "../components/Master";
import Precio from "../components/Precio";
import Noticias from '../components/Noticias'

const Index = props => (
  <MasterPage>
    <div className="row">
      <div className="col-12">
        <h2>Precio Bitcoin</h2>
        <Precio precio={props.precio} />
      </div>
      <div className="col-md-8">
        <h2>Noticias sobre Bitcoin</h2>}
        {/* <Noticias noticias={noticias} /> */}
      </div>
      <div className="col-md-4">
        <h2>Pr√≥ximos eventos de Bitcoin</h2>
      </div>
    </div>
  </MasterPage>
);

Index.getInitialProps = async () => {
  const precio = await fetch(`https://api.coinmarketcap.com/v2/ticker/1/`);
  const noticias = await fetch(
    `https://newsapi.org/v2/top-headlines?country=us&category=bitcoin&apiKey=4a7109eb2bc54b6a89cd9557a2803d7b&language=es`
  );

  const resPrecio = await precio.json();
  const resNoticias = await noticias.json();

  return {
    precio: resPrecio.data.quotes.USD,
    noticias: resNoticias.articles
  };
};

export default Index;
